const path = require( "path" );
const mimeTypes = {
		"css" : "text/css",
		"gif": "image/gif",
		"html": "text/html",
		"ico": "image/x-icon",
		"jpeg": "image/jpeg",
		"jpg": "image/jpg",
		"js": "text/javascript",
		"json" : "application/json",
		"pdf" : "application/pdf",
		"png" : "image/png",
		"svg" : "image/svg+xml",
		"swf" : "application/x-shockwave-flash",
		"tiff" : "image/tiff",
		"txt" : "text/plain",
		"wav" : "audio/x-wav",
		"wma" : "audio/x-ms-wma",
		"wmv" : "video/x-ms-wmv",
		"xml" : "text/xml"
};
module.exports = ( filePath ) => {
		let ext = path.extname( filePath ).slice( 1 ).toLowerCase();// 取没有点的文件后缀
		if( !ext ) ext = filePath;	//	类似.gitignore文件或者是没有后缀的文件返回空,则把文件名当成拓展名
		return mimeTypes[ ext ] || mimeTypes[ "txt" ];
};
// path.extname()方法返回path的扩展名,即从path最后最后一个.(句号)字符到字符串结束.
//		如果path最后一部分没有.或path的文件名(见path.basename())的第一个字符是'.',则返回一个空字符串.
// path.extname('index.html');		//	返回:'.html'
// path.extname('index.coffee.md');	//	返回: '.md'
// path.extname('index.');			//	返回: '.'
// path.extname('index');			//	返回: ''
// path.extname('.index');			//	返回: ''
