// cli工具
const yargs = require( "yargs" );
const Server = require( "./app.js" );

const argv = yargs.usage( "anyd [options]" ).option( "p" , {	// usage提示
	alias : "port",	// 别名
	describe : "端口号",	// 描述
	default : 8090	// 默认
} ).option( "h" , {
	alias : "host",
	describe : "host",
	default : "localhost"
} ).option( "d" , {
	alias : "root",
	describe : "root path",
	default : process.cwd()
} ).version().alias( "v" , "vewrsion" ).help().argv;

const server = new Server( argv );
server.start();
